            <label htmlFor="name">Name</label>
            <input type="text" id="name" placeholder="HongGildong" value="" onChange={onChangeName}/>
            {isValidName ? <p className="warning" id="warning-name">The name's length must be between 1 ~ 30.</p> : <p/>}
          </div>

          <div className="input-element">
            <label htmlFor="phonenumber">Phone Number</label>
            <input type="text" id="phonenumber" placeholder="xxx-xxxx-xxxx" value="" onChange={onChangePhone}/>
            {isValidPhonenumber ? <p className="warning" id="warning-phonenumber">Invalid form of Phone Number.</p> : <p/>}
          </div>

          <div className="input-element">
            <label htmlFor="birthday">Birthday</label>
            <input type="text" id="birthday" placeholder="00/00" value="" onChange={onChangeBirth}/>
            {isValidBirthday ? <p className="warning" id="warning-birthday">Invalid form of birthday.</p> : <p/>}
          </div>

          <div className="input-element">
            <label htmlFor="relation">Relation</label>
            <input type="text" id="relation" placeholder="coworker" value="" onChange={onChangeRelation}/>
